// Отримуємо дані з попередньої ноди
const stdout = $input.first().json.stdout || '';
const stderr = $input.first().json.stderr || '';
const exitCode = $input.first().json.exitCode;
const credentialId = $input.first().json.credentialId;
const credentialName = $input.first().json.credentialName;

// Перевірка на помилки
if (exitCode !== 0) {
  return {
    json: {
      error: true,
      credentialId,
      credentialName,
      message: 'Export command failed',
      stderr: stderr.substring(0, 500),
      exitCode
    }
  };
}

if (!stdout) {
  return {
    json: {
      error: true,
      credentialId,
      credentialName,
      message: 'No output from export command'
    }
  };
}

try {
  // Знаходимо, де починається JSON (символ '[')
  const jsonStartIndex = stdout.indexOf('[');

  if (jsonStartIndex === -1) {
    return {
      json: {
        error: true,
        credentialId,
        credentialName,
        message: 'No JSON found in output',
        stdout: stdout.substring(0, 500)
      }
    };
  }

  // Відрізаємо все зайве до JSON
  const jsonString = stdout.substring(jsonStartIndex);

  // Перетворюємо текст на справжній JSON
  const result = JSON.parse(jsonString);

  // result - це масив, беремо перший елемент
  const credential = Array.isArray(result) ? result[0] : result;

  if (!credential) {
    return {
      json: {
        error: true,
        credentialId,
        credentialName,
        message: 'Empty result from export'
      }
    };
  }

  // Повертаємо успішний результат
  return {
    json: {
      success: true,
      id: credential.id,
      name: credential.name,
      type: credential.type,
      data: credential.data,
      createdAt: credential.createdAt,
      updatedAt: credential.updatedAt,
      isManaged: credential.isManaged
    }
  };

} catch (error) {
  return {
    json: {
      error: true,
      credentialId,
      credentialName,
      message: 'Failed to parse JSON',
      errorDetails: error.message,
      stdout: stdout.substring(0, 500)
    }
  };
}

// Збираємо всі результати
const allItems = $input.all();

const results = {
  successful: [],
  failed: [],
  skipped: [],
  total: allItems.length
};

for (const item of allItems) {
  const credName = item.json.name || item.json.credentialName || 'Unknown';
  const credId = item.json.id || item.json.credentialId || 'Unknown';

  if (item.json.error) {
    results.failed.push({
      id: credId,
      name: credName,
      reason: item.json.message || 'Unknown error'
    });
  } else if (item.json.success) {
    results.successful.push({
      id: credId,
      name: credName
    });
  } else {
    results.skipped.push({
      id: credId,
      name: credName
    });
  }
}

return {
  json: {
    summary: {
      total: results.total,
      successful: results.successful.length,
      failed: results.failed.length,
      skipped: results.skipped.length
    },
    details: results,
    timestamp: new Date().toISOString()
  }
};
